INSERT INTO USERS
(ID, IDENTIFICATION, PASSWORD, NAME, PHONENUMBER, ADDRESS, STATUS, ROLL, CREATEDDATE, PROFILEIMAGENAME, PROFILEIMAGESIZE, PROFILEIMAGEUUID, PROFILEIMAGEPATH, SAVINGLEVEL_ID, REGISTERED_KAKAO, REGISTERED_NAVER)
VALUES(SEQ_USER.NEXTVAL, #{identification},#{password},#{name},#{phoneNumber},#{address}, default, default,#{roll},#{profileImageName,jdbcType=VARCHAR}, #{profileImageSize,jdbcType=NUMERIC},#{profileImagePath,jdbcType=VARCHAR},#{profileImageUuid,jdbcType=VARCHAR},#{levelId}, default, default);
